from RSA.py import coder_key_open
from RSA.py import coder_key_sec

import unitest

class RSATest(unittest.TestCase):
    def test1(self):
        self.assertEqual(coder_key_open("привет, проблемы есть?",89,5917),[4320, 4142, 4836, 1858, 642, 5391, 323, 2663, 4320, 4142, 127, 4187, 4274, 642, 3002, 3051, 2663, 642, 1186, 5391, 518, 457])

    def test2(self):
        self.assertEqual(coder_key_sec("4320, 4142, 4836, 1858, 642, 5391, 323, 2663, 4320, 4142, 127, 4187, 4274, 642, 3002, 3051, 2663, 642, 1186, 5391, 518, 457",3689,5917),"привет, проблемы есть?")

    def test3(self):
        self.assertEqual(coder_key_open("Обычный рецепт блинчиков состоит из: муки, молока, масла, соды и уксуса, солии сахара",83,3053),[647, 1010, 1514, 329, 529, 1514, 2917, 2576, 2590, 839, 2012, 839, 2340, 1141, 2576, 1010, 1790, 972, 529, 329, 972, 2144, 1853, 2880, 2576, 2276, 1853, 2276, 1141, 1853, 972, 1141, 2576, 972, 2247, 1571, 2576, 2088, 680, 2144, 972, 345, 2576, 2088, 1853, 1790, 1853, 2144, 1519, 345, 2576, 2088, 1519, 2276, 1790, 1519, 345, 2576, 2276, 1853, 560, 1514, 2576, 972, 2576, 680, 2144, 2276, 680, 2276, 1519, 345, 2576, 2276, 1853, 1790, 972, 972, 2576, 2276, 1519, 958, 1519, 2590, 1519])

    def test4(self):
        self.assertEqual(coder_key_sec("647, 1010, 1514, 329, 529, 1514, 2917, 2576, 2590, 839, 2012, 839, 2340, 1141, 2576, 1010, 1790, 972, 529, 329, 972, 2144, 1853, 2880, 2576, 2276, 1853, 2276, 1141, 1853, 972, 1141, 2576, 972, 2247, 1571, 2576, 2088, 680, 2144, 972, 345, 2576, 2088, 1853, 1790, 1853, 2144, 1519, 345, 2576, 2088, 1519, 2276, 1790, 1519, 345, 2576, 2276, 1853, 560, 1514, 2576, 972, 2576, 680, 2144, 2276, 680, 2276, 1519, 345, 2576, 2276, 1853, 1790, 972, 972, 2576, 2276, 1519, 958, 1519, 2590, 1519)",2267,3053),"Обычный рецепт блинчиков состоит из: муки, молока, масла, соды и уксуса, солии сахара")

    def test5(self):
        self.assertEqual(coder_key_open("кресло, стул, подущка, run boy run and people fly",53,1891),[919, 1654, 1069, 64, 829, 838, 551, 528, 64, 1854, 1359, 829, 551, 528, 721, 838, 964, 1359, 487, 919, 348, 551, 528, 451, 1323, 106, 528, 831, 782, 609, 528, 451, 1323, 106, 528, 188, 106, 537, 528, 1776, 1008, 782, 1776, 1050, 1008, 528, 1516, 1050, 609])

    def test6(self):
        self.assertEqual(coder_key_sec("919, 1654, 1069, 64, 829, 838, 551, 528, 64, 1854, 1359, 829, 551, 528, 721, 838, 964, 1359, 487, 919, 348, 551, 528, 451, 1323, 106, 528, 831, 782, 609, 528, 451, 1323, 106, 528, 188, 106, 537, 528, 1776, 1008, 782, 1776, 1050, 1008, 528, 1516, 1050, 609",917,1891),"кресло, стул, подущка, run boy run and people fly")
if __name__ == "__main__":
    unittest.main()